import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./Login.scss";

export const Login = (props) => {
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here (e.g., validate credentials with a server)

    // For demonstration purposes, let's simulate a successful login with hardcoded credentials
    const mockUser = {
      email: "testuser@example.com",
      password: "testpassword",
    };

    if (email === mockUser.email && pass === mockUser.password) {
      // Redirect to the homepage after successful login
      history.push("/"); // Replace "/" with the actual path to the homepage
    } else {
      alert("Invalid credentials. Please try again.");
    }
  }

  return (
    <div className="auth-form-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <label htmlFor="email">Email</label>
        <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@gmail.com" id="email" name="email" />
        <label htmlFor="password">Password</label>
        <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="********" id="password" name="password" />
        <button type="submit">Log In</button>
      </form>
      <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account? Register here.</button>
    </div>
  )
};
